(function(){tinymce.PluginManager.requireLangPack('preview2');tinymce.create('tinymce.plugins.Preview2',{init:function(ed,url){var options=ed.settings.preview2_options;var element=options.source_element;var oldContent=element?element.innerHTML:'';var showCallback=options.onPreview;if(typeof showCallback=='string'){showCallback=tinymce.resolve(showCallback)}ed.addCommand('mcePreview2',function(){if(element){element.innerHTML=ed.getContent();if(showCallback){showCallback(ed)}}else{alert(ed.getLang('preview2.undefined_element'))}});ed.addCommand('mcePreview2_cancel',function(){if(element){element.innerHTML=oldContent}else{alert(ed.getLang('preview2.undefined_element'))}});ed.addButton('preview2',{title:'preview2.desc',cmd:'mcePreview2',image:url+'/img/preview2.gif'})},getInfo:function(){return{longname:'Preview2',author:'Yanick Rochon',authorurl:'http://www.majisti.com',infourl:'http://',version:"0.1"}}});tinymce.PluginManager.add('preview2',tinymce.plugins.Preview2)})();